import UIKit
import DGElasticPullToRefresh
import CellAnimator
import ZoomTransitioning

class MainViewController: UIViewController, UITableViewDelegate, UITableViewDataSource {

    // MARK: - Instance variables
    
    var modelArray = [model]()

    var cellId = "cellId"
    
    var refreshColor = UIColor(red: 57/255.0, green: 67/255.0, blue: 89/255.0, alpha: 1.0)
    
    fileprivate var selectedImageView: UIImageView?
    
    // MARK: - Interface
    
    @IBOutlet weak var tableView: UITableView!
    
    // MARK: - Life Cycle
    
    override func viewDidLoad() {
        super.viewDidLoad()
        
        dummyModel()
        
        let loadingView = DGElasticPullToRefreshLoadingViewCircle()
        loadingView.tintColor = UIColor.white
        tableView.dg_addPullToRefreshWithActionHandler({ [weak self] () -> Void in
            DispatchQueue.main.asyncAfter(deadline: DispatchTime.now() + Double(Int64(1.5 * Double(NSEC_PER_SEC))) / Double(NSEC_PER_SEC), execute: {
                self?.tableView.reloadData()
                self?.tableView.dg_stopLoading()
            })
            }, loadingView: loadingView)
        tableView.dg_setPullToRefreshFillColor(refreshColor)
        tableView.dg_setPullToRefreshBackgroundColor(tableView.backgroundColor!)
   
    }
    
    deinit {
        tableView.dg_removePullToRefresh()
    }
    
    // MARK: - TableView Data Source
    
    func numberOfSections(in tableView: UITableView) -> Int {
        
        return 1
    }
    
    func tableView(_ tableView: UITableView, numberOfRowsInSection section: Int) -> Int {
        
        return modelArray.count
    }
    
    func tableView(_ tableView: UITableView, cellForRowAt indexPath: IndexPath) -> UITableViewCell {
        
        let cell = tableView.dequeueReusableCell(withIdentifier: cellId, for: indexPath) as! MainViewCell
        
        cell.modelCell = modelArray[indexPath.item]
        
        return cell
    }
    
    func tableView(_ tableView: UITableView, didSelectRowAt indexPath: IndexPath) {
        
        let cell = tableView.cellForRow(at: indexPath) as! MainViewCell
        selectedImageView = cell.imageModel
        
        performSegue(withIdentifier: "detailSegue", sender: indexPath)
    }
    
    func tableView(_ tableView: UITableView, willDisplay cell: UITableViewCell, forRowAt indexPath: IndexPath) {
        
        CellAnimator.animateCell(cell: cell, withTransform: CellAnimator.TransformWaveTop, andDuration: 0.75)
    }

    // MARK: - Transition between views
    
    override func prepare(for segue: UIStoryboardSegue, sender: Any?) {
        
        if segue.identifier == "detailSegue" {
            
            let destination = segue.destination as? DetailViewController
            //the below line is incorrect and should be something else
            let selectedIndexPath = sender as! NSIndexPath
            let index = modelArray[selectedIndexPath.row]
            //pass data on using the index
            destination?.detailModel = index
        }
    }

